mapper.select:
select

from district
where 0=0 

	and districtId = #{districtid,jdbcType=INTEGER}


	and user_id= #{userId,jdbcType=INTEGER}



	and districtStatus=#{districtstatus,jdbcType=INTEGER}



 and distrctName=#{distrctname,jdbcType=VARCHAR}



limit #{page,jdbcType=INTEGER} ,#{pageSize,jdbcType=VARCHAR}

mapper.update:
update district
set distrctName = #{distrctname,jdbcType=VARCHAR},
user_id = #{userId,jdbcType=INTEGER},
districtStatus = #{districtstatus,jdbcType=INTEGER}
where districtId = #{districtid,jdbcType=INTEGER}
